package main

import (
	"github.com/groundcover-com/pulumi-groundcover/sdk/go/groundcover"
	"github.com/pulumi/pulumi/sdk/v3/go/pulumi"
	"github.com/pulumi/pulumi/sdk/v3/go/pulumi/config"
)

func main() {
	pulumi.Run(func(ctx *pulumi.Context) error {
		cfg := config.New(ctx, "")
		groundcoverApiKey := cfg.Require("groundcoverApiKey")
		groundcoverBackendId := cfg.Require("groundcoverBackendId")
		// Example Policy (required for Service Account)
		examplePolicy, err := groundcover.NewPolicy(ctx, "examplePolicy", &groundcover.PolicyArgs{
			Role: pulumi.StringMap{
				"read": pulumi.String("read"),
			},
			Description: pulumi.String("Example policy for api key resource example."),
		})
		if err != nil {
			return err
		}
		// Example Service Account (required for API Key)
		exampleServiceaccount, err := groundcover.NewServiceaccount(ctx, "exampleServiceaccount", &groundcover.ServiceaccountArgs{
			PolicyUuids: pulumi.StringArray{
				examplePolicy.Uuid,
			},
		})
		if err != nil {
			return err
		}
		// Example API Key
		exampleApikey, err := groundcover.NewApikey(ctx, "exampleApikey", &groundcover.ApikeyArgs{
			Description:      pulumi.String("API Key generated via Terraform example."),
			ServiceAccountId: exampleServiceaccount.ID(),
		})
		if err != nil {
			return err
		}
		ctx.Export("apikeyExampleId", exampleApikey.ID())
		ctx.Export("apikeyExampleValue", exampleApikey.ApiKey)
		ctx.Export("apikeyExampleCreationDate", exampleApikey.CreationDate)
		return nil
	})
}
