import java.nio.file.Paths;

public class App {
    public static void main(String[] args) {
        Pulumi.run(App::stack);
    }

    public static void stack(Context ctx) {
        final var config = ctx.config();
        final var groundcoverApiKey = config.get("groundcoverApiKey");
        final var groundcoverBackendId = config.get("groundcoverBackendId");
        // Example Policy (required for Service Account)
        var examplePolicy = new Policy("examplePolicy", PolicyArgs.builder()
            .role(Map.of("read", "read"))
            .description("Example policy for api key resource example.")
            .build());

        // Example Service Account (required for API Key)
        var exampleServiceaccount = new Serviceaccount("exampleServiceaccount", ServiceaccountArgs.builder()
            .policyUuids(examplePolicy.uuid())
            .build());

        // Example API Key
        var exampleApikey = new Apikey("exampleApikey", ApikeyArgs.builder()
            .description("API Key generated via Terraform example.")
            .serviceAccountId(exampleServiceaccount.id())
            .build());

        // Optional: Set an expiration date (RFC3339 format)
        // expiration_date  = "2025-01-01T00:00:00Z"
        ctx.export("apikeyExampleId", exampleApikey.id());
        ctx.export("apikeyExampleValue", exampleApikey.apiKey());
        ctx.export("apikeyExampleCreationDate", exampleApikey.creationDate());
    }
}
