const groundcoverBackendId = config.require("groundcoverBackendId");
// Example Dashboard: Basic metrics dashboard
const metricsDashboard = new groundcover.Dashboard("metricsDashboard", {
    description: "Example dashboard showing system metrics",
    team: "platform",
    preset: JSON.stringify({
        duration: "Last 1 hour",
        layout: [
            {
                id: "A",
                x: 0,
                y: 0,
                w: 12,
                h: 4,
                minH: 2,
            },
            {
                id: "B",
                x: 0,
                y: 4,
                w: 6,
                h: 4,
                minH: 2,
            },
            {
                id: "C",
                x: 6,
                y: 4,
                w: 6,
                h: 4,
                minH: 2,
            },
        ],
        widgets: [
            {
                id: "A",
                type: "widget",
                name: "CPU Usage",
                queries: [{
                    id: "A",
                    expr: "avg(rate(container_cpu_usage_seconds_total[5m])) * 100",
                    dataType: "metrics",
                    step: null,
                    editorMode: "builder",
                }],
                visualizationConfig: {
                    type: "time-series",
                },
            },
            {
                id: "B",
                type: "widget",
                name: "Memory Usage",
                queries: [{
                    id: "B",
                    expr: "avg(container_memory_usage_bytes)",
                    dataType: "metrics",
                    step: null,
                    editorMode: "builder",
                }],
                visualizationConfig: {
                    type: "gauge",
                },
            },
            {
                id: "C",
                type: "text",
                html: "<h3>System Metrics</h3><p>This dashboard shows key system metrics including CPU and memory usage.</p>",
            },
        ],
        variables: {},
        schemaVersion: 3,
    }),
});
// Example Dashboard: Simple monitoring dashboard
const simpleDashboard = new groundcover.Dashboard("simpleDashboard", {preset: JSON.stringify({
    duration: "Last 30 minutes",
    layout: [{
        id: "widget1",
        x: 0,
        y: 0,
        w: 12,
        h: 6,
        minH: 3,
    }],
    widgets: [{
        id: "widget1",
        type: "widget",
        name: "Request Rate",
        queries: [{
            id: "query1",
            expr: "sum(rate(http_requests_total[5m]))",
            dataType: "metrics",
            step: null,
            editorMode: "code",
        }],
        visualizationConfig: {
            type: "time-series",
            config: {
                showLegend: true,
                unit: "ops",
            },
        },
    }],
    variables: {},
    schemaVersion: 3,
})});
export const metricsDashboardId = metricsDashboard.id;
export const metricsDashboardOwner = metricsDashboard.owner;
export const simpleDashboardId = simpleDashboard.id;
export const simpleDashboardOwner = simpleDashboard.owner;
