    description: "This policy is managed by Terraform.",
    claimRole: "sso-admin-role",
    role: {
        admin: "admin",
    },
    dataScope: {
        simple: {
            operator: "and",
            conditions: [
                {
                    key: "cluster",
                    origin: "root",
                    type: "string",
                    filters: [{
                        op: "match",
                        value: "production-cluster",
                    }],
                },
                {
                    key: "namespace",
                    origin: "root",
                    type: "string",
                    filters: [{
                        op: "not_match",
                        value: "kube-system",
                    }],
                },
            ],
        },
    },
});
export const policyUuid = myPolicy.uuid;
export const policyRevisionNumber = myPolicy.revisionNumber;
